package com.ahom.hrms.controller;import com.ahom.hrms.Response.ResponseHandler;import com.ahom.hrms.service.SalarySlipService;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.http.HttpStatus;import org.springframework.http.ResponseEntity;import org.springframework.web.bind.annotation.*;import java.text.ParseException;@RestController@RequestMapping("/salarySlip")public class SalarySlipController {    @Autowired    SalarySlipService salarySlipService;    @PostMapping("/save")    public ResponseEntity<Object>save(@RequestParam String month) throws ParseException {        return ResponseHandler.responseBuilder("generated", HttpStatus.OK                ,salarySlipService.saveSalary(month));    }}