package com.ahom.hrms.entities;import lombok.AllArgsConstructor;import lombok.Getter;import lombok.NoArgsConstructor;import lombok.Setter;import javax.persistence.Entity;import javax.persistence.GeneratedValue;import javax.persistence.GenerationType;import javax.persistence.Id;import javax.validation.constraints.NotBlank;@Entity@Getter@Setter@NoArgsConstructor@AllArgsConstructorpublic class ApplyForWfh {    @Id    @GeneratedValue(strategy = GenerationType.IDENTITY)    private int id;    @NotBlank(message = "field is mandatory")    private String employeeId;    @NotBlank(message = "field is mandatory")    private String name;    @NotBlank(message = "field is mandatory")    private String description;    private String status;}