package com.ahom.hrms.entities;import lombok.Data;import javax.persistence.Entity;import javax.persistence.GeneratedValue;import javax.persistence.GenerationType;import javax.persistence.Id;import java.time.LocalTime;@Entity@Datapublic class Notification {    @Id    @GeneratedValue(strategy = GenerationType.IDENTITY)    private int id;    private String message;    private boolean status;    private LocalTime time;}