package com.ahom.hrms.entities;import lombok.AllArgsConstructor;import lombok.Getter;import lombok.NoArgsConstructor;import lombok.Setter;import javax.persistence.*;import javax.validation.constraints.NotBlank;import javax.validation.constraints.NotEmpty;@Entity@Setter@Getter@AllArgsConstructor@NoArgsConstructorpublic class Termination {    @Id    @GeneratedValue(strategy = GenerationType.IDENTITY)    private int id;    @NotBlank(message = "field is mandatory")    private String employeeId;    @NotBlank(message = "field is mandatory")    private String employeeName;    @NotBlank(message = "field is mandatory")    private String userName;    @NotBlank(message = "field is mandatory")    @Column(columnDefinition="text",length = 500)    private String description;    @NotEmpty(message = "field is mandatory")    private String terminationDate;}