package com.ahom.hrms.serviceimpl;import com.ahom.hrms.Repository.ApplyForWfhRepository;import com.ahom.hrms.Repository.NotificationRepository;import com.ahom.hrms.entities.ApplyForWfh;import com.ahom.hrms.entities.Notification;import com.ahom.hrms.service.ApplyForWfhService;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Service;import javax.validation.Valid;@Servicepublic class ApplyForWfhServiceImpl  implements ApplyForWfhService {    @Autowired    ApplyForWfhRepository applyForWfhRepository ;    @Autowired    private NotificationRepository notificationRepository;    @Override    public ApplyForWfh save(@Valid ApplyForWfh applyForWfh) {        Notification notification=new Notification();        notification.setMessage("New WFH request ");        notificationRepository.save(notification);        return applyForWfhRepository.save(applyForWfh);    }    @Override    public Object delete(int id) {         applyForWfhRepository.deleteById(id);        return null;    }    @Override    public ApplyForWfh update(ApplyForWfh applyForWfh, String employeeId) {        ApplyForWfh applyForWfh1=applyForWfhRepository.findByEmployeeId(employeeId);        if (employeeId!=null){            ApplyForWfh applyForWfh2=new ApplyForWfh();            applyForWfh2.setId(applyForWfh1.getId());            applyForWfh2.setDescription(applyForWfh.getDescription());            applyForWfh2.setStatus(applyForWfh.getStatus());            applyForWfh2.setName(applyForWfh.getName());            applyForWfh2.setEmployeeId(applyForWfh.getEmployeeId());            applyForWfhRepository.save(applyForWfh2);        }        return applyForWfh;    }    @Override    public ApplyForWfh getByEmployeeId(String employeeId) {        return applyForWfhRepository.findByEmployeeId(employeeId);    }}