package com.ahom.hrms.serviceimpl;import com.ahom.hrms.Repository.NotificationRepository;import com.ahom.hrms.entities.Notification;import com.ahom.hrms.service.NotificationService;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Service;import java.time.LocalTime;import java.time.format.DateTimeFormatter;import java.util.List;@Servicepublic class NotificationServiceImpl implements NotificationService {    @Autowired    NotificationRepository notificationRepository;    @Override    public Notification saveNotification(Notification notification) {        LocalTime localTime=LocalTime.now();        DateTimeFormatter formatter=DateTimeFormatter.ofPattern("HH:mm");        String format = formatter.format(localTime);        System.out.println(format+ "localTime");        notification.setTime(format);        return notificationRepository.save(notification);    }    @Override    public Notification updateNotification(int id, Notification notification) {        Notification notification1 = notificationRepository.findById(id).orElse(null);        if (notification1 != null){            notification1.setStatus(notification.isStatus());            notificationRepository.save(notification1);        }        return notification;    }    @Override    public List<Notification> getAllNotification() {        return notificationRepository.findAll();    }}